mapper.select:
SELECT
	,
	ro.office_id AS "officeList.id"
FROM [10.5.60.80].[jeesite-yfc].[dbo].[sys_role] a
LEFT JOIN [10.5.60.80].[jeesite-yfc].[dbo].[sys_office] o ON o.id = a.office_id
LEFT JOIN [10.5.60.80].[jeesite-yfc].[dbo].[sys_user_role] ur ON ur.role_id = a.id
LEFT JOIN [10.5.60.80].[jeesite-yfc].[dbo].[sys_user] u ON u.id = ur.user_id
LEFT JOIN [10.5.60.80].[jeesite-yfc].[dbo].[sys_role_office] ro ON ro.role_id = a.id
WHERE a.del_flag = #{DEL_FLAG_NORMAL} AND a.useable=#{useable}

	AND u.id = #{user.id}


	AND u.login_name = #{user.loginName}


${sqlMap.dsf}
ORDER BY o.code, a.name
